<script>
    (function() {
        const tabs = document.querySelectorAll('.js-country-tab');
        const panels = document.querySelectorAll('.country-panel');
        const indicator = document.getElementById('tabIndicator');
        const wrap = document.getElementById('countryTabs');

        function setIndicator(btn) {
            const rect = btn.getBoundingClientRect();
            const parentRect = wrap.getBoundingClientRect();
            const left = rect.left - parentRect.left + wrap.scrollLeft;
            indicator.style.left = left + 'px';
            indicator.style.width = rect.width + 'px';
        }

        function activate(target) {
            panels.forEach(p => p.classList.add('hidden'));
            document.getElementById('panel-' + target).classList.remove('hidden');

            tabs.forEach(b => {
                b.classList.remove('bg-gradient-to-r', 'from-[#2f77b1]', 'to-[#3f8ad1]', 'text-white', 'shadow-md');
                if (!b.classList.contains('text-[#2f77b1]')) b.classList.add('text-[#2f77b1]');
            });
            const btn = document.querySelector(`.js-country-tab[data-target="${target}"]`);
            btn.classList.add('bg-gradient-to-r', 'from-[#2f77b1]', 'to-[#3f8ad1]', 'text-white', 'shadow-md');
            btn.classList.remove('text-[#2f77b1]');
            setIndicator(btn);
        }

        tabs.forEach(b => b.addEventListener('click', () => activate(b.dataset.target)));

        // init default
        activate('russia');

        // reposition indicator on resize
        window.addEventListener('resize', () => {
            const active = document.querySelector('.js-country-tab.text-white');
            if (active) setIndicator(active);
        });
    })();
</script>